fd.rendered(function() {
    //disable CompletionDate field
    fd.field('CompletionDate').disabled = true;
    //set CompletionDate to the current date
    fd.field('CompletionDate').value = new Date();


    //call function on form load
    calendarSetUp();

    fd.field('Same').$on('change', mailingAddressSame);
    mailingAddressSame();

});

//disable dates to allow only 18+ users
//set up calendar start view
const calendarSetUp = async () => {

    // Load external libraries
    await $.getScript('https://cdn.jsdelivr.net/npm/luxon@3.5.0/build/global/luxon.min.js');

    // Get today's date
    const today = luxon.DateTime.now();

    const newDate = today.minus({
        years: 18
    });

    fd.field('DateOfBirth').widgetOptions = {
        max: newDate.toJSDate(),
        start: 'decade'
    }
}

//fill out Mailing Address and hide fields
function mailingAddressSame() {
    if (fd.field('Same').value.length > 0) {
        fd.field('MailingStreetAddress').value = fd.field('StreetAddress').value;
        fd.field('MailingStreetAddress1').value = fd.field('StreetAddress1').value;
        fd.field('MailingCity').value = fd.field('City').value;
        fd.field('MailingRegionState').value = fd.field('RegionState').value;
        fd.field('MailingPostalZipCode').value = fd.field('PostalZipCode').value;
        fd.field('MailingCountry').value = fd.field('Country').value;

        fd.field('MailingStreetAddress').disabled = true;
        fd.field('MailingStreetAddress1').disabled = true;
        fd.field('MailingCity').disabled = true;
        fd.field('MailingRegionState').disabled = true;
        fd.field('MailingPostalZipCode').disabled = true;
        fd.field('MailingCountry').disabled = true;
    } else {
        fd.field('MailingStreetAddress').clear();
        fd.field('MailingStreetAddress1').clear();
        fd.field('MailingCity').clear();
        fd.field('MailingRegionState').clear();
        fd.field('MailingPostalZipCode').clear();
        fd.field('MailingCountry').clear();


        fd.field('MailingStreetAddress').disabled = false;
        fd.field('MailingStreetAddress1').disabled = false;
        fd.field('MailingCity').disabled = false;
        fd.field('MailingRegionState').disabled = false;
        fd.field('MailingPostalZipCode').disabled = false;
        fd.field('MailingCountry').disabled = false;
    }
}

