fd.themeChanged = theme => {
    $('.fd-form').attr('theme', theme);
};

fd.rendered(function() {

    //disable VaccinationDate date fields
    fd.field('VaccinationDate').disabled = true;
    //set VaccinationDate to the current date
    fd.field('VaccinationDate').value = new Date();

    //load the Moment.js library to disable dates
    $.getScript('https://cdnjs.cloudflare.com/ajax/libs/moment.js/2.24.0/moment-with-locales.min.js')
        .then(function() {
            birthDateCalendarSetUp();

        });
        
    //set up calendar start view and disable dates
    fd.field('DiagnosedCOVIDDate').widgetOptions = {
        max: new Date(),
        min: new Date('2020-03-01'),
        start: 'decade'
    }
    
    fd.field('ImmunizationDate').widgetOptions = {
        max: new Date(),
        min: new Date('2021-01-01'),
        start: 'decade'
    }

    //call functions when changing field values    
    fd.field('ChronicConditions').$on('change', showHideDiagnosedChronicHealthProblems);
    fd.field('DiagnosedCOVID').$on('change', showHideDiagnosedCOVIDDate);
    fd.field('Immunized').$on('change', showHideImmunizationDate);

    //сalling functions on form loading
    showHideDiagnosedChronicHealthProblems();
    showHideDiagnosedCOVIDDate();
    showHideImmunizationDate();

});

//disable dates to allow only 18+ users
//set up calendar start view
function birthDateCalendarSetUp() {
    var today = moment(new Date());
    var newDate = today.subtract(18, 'years');
    fd.field('BirthDate').widgetOptions = {
        max: moment(newDate).toDate(),
        start: 'decade'
    }
}

//show hide DiagnosedChronicHealthProblems field
function showHideDiagnosedChronicHealthProblems() {
    if (fd.field('ChronicConditions').value == 'Yes') {
        fd.field('DiagnosedChronicHealthProblems').hidden = false;
    } else {
        fd.field('DiagnosedChronicHealthProblems').hidden = true;
    }
}

//show hide DiagnosedCOVIDDate field
function showHideDiagnosedCOVIDDate() {
    if (fd.field('DiagnosedCOVID').value == 'Yes') {
        fd.field('DiagnosedCOVIDDate').hidden = false;
    } else {
        fd.field('DiagnosedCOVIDDate').hidden = true;
    }
}

//show hide ImmunizationDate field
function showHideImmunizationDate() {
    if (fd.field('Immunized').value == 'Yes') {
        fd.field('ImmunizationDate').hidden = false;
    } else {
        fd.field('ImmunizationDate').hidden = true;
    }
}

