fd.rendered(function () {
    //hides container on form load
    $('.cake-shape').hide();
    
    //shows/hides container when the field value is changed
    fd.field('Shape').$on('change',function(value){
        if(value == 'Custom'){
            $('.cake-shape').show();
        }
        else {
            $('.cake-shape').hide();
        }
        
    })
});
