//function that shows/hides container with the DataTable
function friendsList() {
    
    var friends = fd.field('Friends').value;

    if (friends) {
        //show elements
        $('.friends').show();
    }
    else {
        //hide elements
        $('.friends').hide();
    }
}

fd.rendered(function () {
    
    //call the function on form load
    friendsList();
    
    //call function when field is changed
    fd.field('Friends').$on('change', friendsList)
});


