.fd-form .form-container, .form-container.fd-form {
    width: 80% !important;
}

.fd-form {
    background: url(https://plumsailforms.blob.core.windows.net/397ad761-3174-49da-a4bf-a741828fbddb-images/57f671f9-jason-leung-Xaanw0s0pMk-unsplash.jpg) no-repeat center top ;
    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    
}
.fd-form .form-widget > .fd-form {
    background: none;
}

.fd-form .content-background{
    -webkit-backdrop-filter: blur(6px);
    backdrop-filter: blur(6px);
    background-color: #01647b6e;
    color: #000000;
    border-radius: 15px;
}

.fd-form h1, .fd-form h6, .fd-form h4, .fd-form small, .fd-field-title, .fd-form label {
    color: #ffff !important;
}
.fd-form h1 {
    font-size: min(6.5vw, 2.34375rem) !important;
    text-align:center !important;
}

.fd-form iframe {
    width:100%;
    height:300px;
}





.fd-form button.fd-button.btn.btn-primary {
    font-weight: 500 !important
}
