// function that shows/hides Other field
function toggleOtherType() {
    if (fd.field('IncidentType').value.includes('Other')) {
        fd.field('OtherIncidentType').hidden = false;
    } else {
        fd.field('OtherIncidentType').hidden = true;
    }
}

fd.rendered(function () {
    // call function on form load
    toggleOtherType();

    // call function when the field value is changed
    fd.field('IncidentType').$on('change', toggleOtherType);
});
