fd.rendered(function () {

    // The function to show/hide Policyholder section
    function hidePolicyholderSection(value) {
        if(value === 'No'){
            $('.policyholder').show();
        }
        else {
            $('.policyholder').hide();
        }
    }

    // call function when the form is rendered
    hidePolicyholderSection(fd.field('IsTheMember').value);

    // call function when IsTheMember field changes
    fd.field('IsTheMember').$on('change', value => {
        hidePolicyholderSection(value);
    });

});


