fd.themeChanged = theme => {
    $('.fd-form').attr('theme', theme);
};

fd.rendered(function() {
    //disable fields
    fd.field('Age').disabled = true;
    fd.field('CompletionDate').disabled = true;
    //set CompletionDate to the current date
    fd.field('CompletionDate').value = new Date();

    //call functions when changing field values
    fd.field('Medications').$on('change', showHideMedicationsList);
    fd.field('Insurance').$on('change', showHideInsuranceFields);
    fd.field('AllergicOnMedications').$on('change', showHideAllergicOnMedicationsList);
    fd.field('AllergicFood').$on('change', showHideAllergicFood);
    fd.field('AreYouASmoker').$on('change', showHideSmokingDaily);
    fd.field('DateOfBirth').$on('change', ageDescription);

    //сalling functions on form loading
    showHideMedicationsList();
    showHideInsuranceFields();
    showHideAllergicFood();
    showHideSmokingDaily();
    showHideAllergicOnMedicationsList();
    ageDescription();

    //load the Moment.js library to calculate patient's age and disable dates
    $.getScript('https://cdnjs.cloudflare.com/ajax/libs/moment.js/2.24.0/moment-with-locales.min.js')
        .then(function() {
            calculateAge();
            fd.field('DateOfBirth').$on('change', calculateAge);
            calendarSetUp();

        });
});

//show/hide Age field description
function ageDescription () {
    if(!fd.field('DateOfBirth').value){
    fd.field('Age').description = 'Select date of birth'
    }
    else {
        fd.field('Age').description = ''
    }
}


//show/hide insurance details
function showHideInsuranceFields() {
    if (fd.field('Insurance').value == 'Yes') {
        $('.insurance').show();
    } else {
        $('.insurance').hide();
    }
}

//show/hide medications list
function showHideMedicationsList() {
    if (fd.field('Medications').value == 'Yes') {
        fd.control('MedicationsList').hidden = false;
    } else {
        fd.control('MedicationsList').hidden = true;
    }
}

//show hide AllergicOnMedicationsList field
function showHideAllergicOnMedicationsList() {
    if (fd.field('AllergicOnMedications').value == 'Yes') {
        fd.field('AllergicOnMedicationsList').hidden = false;
    } else {
        fd.field('AllergicOnMedicationsList').hidden = true;
    }
}

//show hide AllergicFoodList field
function showHideAllergicFood() {
    if (fd.field('AllergicFood').value == 'Yes') {
        fd.field('AllergicFoodList').hidden = false;
    } else {
        fd.field('AllergicFoodList').hidden = true;
    }
}

//show hide SmokingDaily field
function showHideSmokingDaily() {
    if (fd.field('AreYouASmoker').value == 'Yes') {
        fd.field('SmokingDaily').hidden = false;
    } else {
        fd.field('SmokingDaily').hidden = true;
    }
}

//calculate patient's age
function calculateAge() {
    if (fd.field('DateOfBirth').value) {
        var purchase = moment(fd.field('DateOfBirth').value);
        var delivery = moment(new Date());
        var dateDiff = delivery.diff(purchase, 'years', false);
        fd.field('Age').value = dateDiff;
    } else {
        fd.field('Age').value = '';
    }
}

//disable dates to allow only 18+ users
//set up calendar start view
function calendarSetUp() {
    var today = moment(new Date());
    var newDate = today.subtract(18, 'years');
    fd.field('DateOfBirth').widgetOptions = {
        max: moment(newDate).toDate(),
        start: 'decade'
    }
}

