/* setting the Heading background depending on the theme */
.fd-form h3 > span {
    background-color: var(--fd-primary-rgb) !important;
}

/** styling of the disable Age field **/
.fd-form .age .form-control:disabled {
    background-color: transparent;
    border: none;
}

/** styling of the disable Date field **/
.fd-form .completion-date .form-control:disabled,
.fd-form .completion-date .form-control[readonly],
.fd-form .completion-date .k-datepicker .k-picker-wrap,
.fd-form .completion-date  .form-control{
    background-color: transparent;
    border: none;
    font-weight: bold;
    padding-left: 0px;
}

.fd-form .completion-date .k-select {
    display: none !important;
}

