//detect theme change
fd.themeChanged = theme => {
    $('.fd-form').attr('theme', theme);
}

fd.rendered(function() {

    //disable registration date fields
    fd.field('RegistrationDate').disabled = true;
    //set RegistrationDate to the current date
    fd.field('RegistrationDate').value = new Date();

    //сalling functions on form loading
    showHideMedicationsList();
    showHideAllergicOnMedicationsList();
    showHideAllergicFood();
    smoker();
    alcohol();
    calendarSetUp();

    //call functions when changing field values    
    fd.field('AllergicMedications').$on('change', showHideAllergicOnMedicationsList);
    fd.field('AllergicFood').$on('change', showHideAllergicFood);
    fd.field('AreYouASmoker').$on('change', smoker);
    fd.field('Alcohol').$on('change', alcohol);
    fd.field('Medications').$on('change', showHideMedicationsList);

});

//show/hide MedicationsList
function showHideMedicationsList() {
    if (fd.field('Medications').value) {
        fd.control('MedicationsList').hidden = false;
    } else {
        fd.control('MedicationsList').hidden = true;
    }
}

//show hide AllergicMedicationsList field
function showHideAllergicOnMedicationsList() {
    if (fd.field('AllergicMedications').value) {
        fd.field('AllergicMedicationsList').hidden = false;
    } else {
        fd.field('AllergicMedicationsList').hidden = true;
    }
}
//show hide AllergicFood field
function showHideAllergicFood() {
    if (fd.field('AllergicFood').value) {
        fd.field('AllergicFoodList').hidden = false;
    } else {
        fd.field('AllergicFoodList').hidden = true;
    }
}
//show/hide questions about smoking
function smoker() {
    if (fd.field('AreYouASmoker').value) {
        $('.smoker').show()
    } else {
        $('.smoker').hide()
    }
}
//show/hide questions about alcohol
function alcohol() {
    if (fd.field('Alcohol').value == 'Occasionally' || fd.field('Alcohol').value == 'Often') {
        $('.alcohol').show()
    } else {
        $('.alcohol').hide()
    }
}

//disable dates to allow only 18+ users
//set up calendar start view
const calendarSetUp = async () => {

    // Load external libraries
    await $.getScript('https://cdn.jsdelivr.net/npm/luxon@3.5.0/build/global/luxon.min.js');

    // Get today's date
    const today = luxon.DateTime.now();

    const newDate = today.minus({
        years: 18
    });

    fd.field('DateOfBirth').widgetOptions = {
        max: newDate.toJSDate(),
        start: 'decade'
    }
}

