js:
window.fd = fd;

//requires to list the names of all the tenants
function occupants() {
    var occupantsNumber = fd.field('NumberOfTenants').value;
    
    fd.validators.length = 0;
    
    if(occupantsNumber > 0) {
        $('.occupants').show();
        
        fd.validators.push({
            name: 'OccupantsValidation',
            error: "List all occupants ",
            validate: function(value) {
                if (fd.control('Occupants').value.length < occupantsNumber)
                    return false;
        
                return true;
            }
        });
    }
    else {
        $('.occupants').hide();
        
    }
}
//function that shows/hides PetInformation field and makes it required
function petInfo() {
    if (fd.field('Pets').value) {
        // Show the PetInformationfield
        $(fd.field('PetInformation').$parent.$el).show();
        fd.field('PetInformation').required = true;
    } else {
        // Hide the PetInformation field
        $(fd.field('PetInformation').$parent.$el).hide();
        fd.field('PetInformation').required = false;
    }
}

fd.rendered(function () {
    
    //hide DataTable on form load
    $('.occupants').hide();
    
    
    //show only future dates
    var today = new Date();
    var minDate = today.setDate(today.getDate()+2);
    fd.field('MoveInDate').widgetOptions = {
        min: new Date(minDate)
    }
    
    //show hide occupants table and add table validation 
    
    //on form load
    occupants();
    //when the number is chagned
    fd.field('NumberOfTenants').$on('change',occupants);
    
    //show/hide PetInformation field and make it required
    
    //on form load
    petInfo();
    
    //when Pets field is changed
    fd.field('Pets').$on('change', petInfo);
    
});

/*****************************************************************************
The JavaScript code from this section will be executed while loading
the current form.

The following predefined variables can be utilized in the code:

fd  - instance of the current form
$   - jQuery object

==============================================================================

fd.rendered(function () {
    // This code is executed once the form is rendered

    // Hide State field for all values of Country field except 'USA'
    function updateStateVisibility() {
        var stateContainer = $(fd.field('State').$el).closest('.form-group');
        if (fd.field('Country').value === 'USA') {
            stateContainer.show();
        } else {
            stateContainer.hide();
        }
    }

    // Set initial State visibility
    updateStateVisibility();

    // Update State visibility after changing Country
    fd.field('Country').$on('change', updateStateVisibility)
});

fd.beforeSave(function () {
    // This code is exeuted before saving the form and
    // may return Promise. The saving does not proceed until
    // the Promise is resolved. If the Promise is rejected,
    // the saving interrupts. This is the appropriate place
    // for adding custom validation.

    // Prevent saving if Start Date is greater than End Date
    if (fd.field('StartDate').value > fd.field('EndDate').value) {
        throw Error('Start Date must not be greater than End Date.');
    }
});

fd.saved(function (result) {
    // This code is executed after saving the form

    // Forwarding users to specific URL
    window.location = '--- Some URL ---';
});

