//function to show/hide details for delivery
function showHideDelivery() {
    if (fd.field('Delivery').value == 'Delivery') {
        fd.field('Address').hidden = false;
        fd.field('Building').hidden = false;
        fd.field('Apartment').hidden = false;
        fd.field('Instructions').hidden = false;
    } else {
        fd.field('Address').hidden = true;
        fd.field('Building').hidden = true;
        fd.field('Apartment').hidden = true;
        fd.field('Instructions').hidden = true;
    }
}

fd.rendered(function() {

    //disable Total field
    fd.field('Total').disabled = true;

    //call function when field value changes
    fd.field('Delivery').$on('change', showHideDelivery);
    //call function on form load
    showHideDelivery();

    //names of table controls
    let menuCategories = ['Appetizers', 'MainCourses', 'Desserts'];

    //customize each table control
    menuCategories.forEach((category) => {


        let dt = fd.control(category)


        // make Price column read-only
        const unitPriceColumn = dt.columns.find(c => c.field === 'Price');
        unitPriceColumn.editable = () => false;

        dt.$on('change', function(value) {
            //variable to count total
            var categoryTotal = 0.0;

            if (value) {
                //go through each row one by one
                for (var i = 0; i < value.length; i++) {

                    if (value[i].Item) {
                        // populate Price column
                        value[i].set('Price', menu[value[i].Item] || 0);
                    }
                    

                    //calculate total for the table
                    if (value[i].Total) {
                        categoryTotal += parseFloat(value[i].Total);
                    }
                }
            }
            //set the category total 
            fd.field(category + 'Total').value = categoryTotal;
        });

        //hide category total 
        fd.field(category + 'Total').hidden = true;

        //calculate total for the whole order
        fd.field(category + 'Total').$on('change', function() {
            fd.field('Total').value = fd.field('AppetizersTotal').value + fd.field('MainCoursesTotal').value + fd.field('DessertsTotal').value;
        })
    })


});

