//function that shows/hides ArrivalTime and FlightNumber fields and makes them required
function transfer() {
    var pickUpRequired = fd.field('PickUp').value
    
    if(pickUpRequired) {
        fd.field('ArrivalTime').required = true;
        fd.field('FlightNumber').required = true;
        
        $(fd.field('ArrivalTime').$parent.$el).show();
        $(fd.field('FlightNumber').$parent.$el).show()
    }
    else {
        fd.field('ArrivalTime').required = false;
        fd.field('FlightNumber').required = false;
        
        $(fd.field('ArrivalTime').$parent.$el).hide();
        $(fd.field('FlightNumber').$parent.$el).hide();
    }
}

fd.rendered(function () {
    
    fd.validators;
    //requires a valid time interval to be entered
    fd.validators.push({
        name: 'Start and End time validation',
        error: "Please select valid start and end time",
        validate: function(value) {
            if (fd.field('CheckInDate').value > fd.field('CheckOutDate').value)
                return false;
    
            return true;
        }
    });
    
    
    //call the function on form load
    transfer();
    
    //call the function when the fild is changed
    fd.field('PickUp').$on('change',transfer);
});

/*****************************************************************************
The JavaScript code from this section will be executed while loading
