window.$ = $;

fd.rendered(function() {

    $('.survey').hide()
    fd.field('WouldYouRecommendUsToAFriend').hidden = true;
    fd.field('EmailToReceiveACouponCode').hidden = true; 
    fd.field('DoYouLikeOurProducts').hidden = true;
    fd.control('Submit2').hidden = true;

 fd.field('DoYouLikeOurProducts').$on('change', function() {
     fd.field('WouldYouRecommendUsToAFriend').hidden = false;
 })

 fd.field('WouldYouRecommendUsToAFriend').$on('change', function() {
     fd.field('EmailToReceiveACouponCode').hidden = false;
     //show control
fd.control('Submit2').hidden = false;
 });

});

css:
.fd-form  {
    box-sizing: border-box;

    -webkit-background-size: cover;
    -moz-background-size: cover;
    -o-background-size: cover;
    background-size: cover;
    width: 80%;
    display: block;
    margin: 0 auto;
 margin-bottom: 20px;
}

.fd-form .form-control {
    color: #000 !important;
}
.fd-form .form-check .form-check-input {
        padding-right: 0px;
    padding-left: 0px;
    padding-top: 0px;
    padding-bottom: 0px;
}

button.fd-button.btn.btn-primary {
    background: white;
    color: #3a3a3a;
        font-weight: 600;
}
.fd-form .btn-primary {
    color: #3a3a3a !important;
}

.fd-form.k-animation-container {
    background-image: none !important;
}

.fd-form h1, .fd-form h4, .fd-form p {
    color: #ffff !important;
}
.fd-form h1, .fd-form p{
    text-align:center!important
}
.fd-form h4 {
  display: flex;
  flex-direction: row;

}
.fd-form h4:before, .fd-form h4:after{
  content: "";
  flex: 1 1;
  border-bottom: 1px solid;
  margin: auto;
}
.fd-form h4:before {
  margin-right: 10px
}
.fd-form h4:after {
  margin-left: 10px
}

.fd-form .total .k-numerictextbox .k-numeric-wrap .k-select {
 display: none;
}
