//show a print over t-shirt mockup
function previewtShirtPrint() {
    fd.field('CustomPrint').hidden = true;
    let tShirtPrint = fd.field('TshirtPrint').value;
  
    if (tShirtPrint == 'Abstract') {
        fd.control('Image2').source = 'https://raw.githubusercontent.com/Plumsail/forms-templates/main/images/abstract.png';
    } else if (tShirtPrint == 'Dragon') {
        fd.control('Image2').source = 'https://raw.githubusercontent.com/Plumsail/forms-templates/main/images/dragon.png';
    } else if (tShirtPrint == 'Flower') {
        fd.control('Image2').source = 'https://raw.githubusercontent.com/Plumsail/forms-templates/main/images/flower.png';
    } else if (tShirtPrint == 'Other') {
        fd.field('CustomPrint').hidden = false;
        if (fd.field('CustomPrint').value.length > 0) {
            fd.control('Image2').source = fd.field('CustomPrint').value[0].url;
        } else {
            fd.control('Image2').source = '';
        }
    }
}

//calculate total for the order
function shirtPrice() {
    let tShirtPrintType = fd.field('TshirtPrint').value;
    fd.field('TShirt').value = 14.59;
  
    if (tShirtPrintType == 'Other') {
        fd.field('Printing').value = 10.99;
    } else if (!tShirtPrintType) {
        fd.field('Printing').value = 0;
    } else {
        fd.field('Printing').value = 5.99;
    }
    fd.field('Total').value = fd.field('TShirt').value + fd.field('Printing').value;
}

fd.rendered(function() {
    
    //disable price fields on form load 
    fd.field('TShirt').disabled = true;
    fd.field('Printing').disabled = true;
    fd.field('Total').disabled = true;
    
    //call functions on form load
    previewtShirtPrint();
    shirtPrice();

    //call functions when field value changes
    fd.field('TshirtPrint').$on('change', previewtShirtPrint);
    fd.field('CustomPrint').$on('change', previewtShirtPrint);
    fd.field('TshirtPrint').$on('change', shirtPrice);
});

