//detect theme change
fd.themeChanged = theme => {
    $('.fd-form').attr('theme', theme);
};

fd.rendered(function() {
    //disable fields
    fd.field('Date').disabled = true;

    //set Date to the current date
    fd.field('Date').value = new Date();

    //call function on form load
    calendarSetUp();

    // add validation for Consent
    fd.field('Consent').addValidator({
        name: 'ConsentValidator',
        error: 'You must confirm all points. If you still have questions, contact your family doctor.',
        validate: function(value) {
            if (fd.field('Consent').value.length < 6)
                return false;

            return true;
        }
    });

    //call functions when changing field values
    fd.field('Allergic').$on('change', showHideAllergicList);
    fd.field('VaccineReaction').$on('change', showHideVaccineReactionList);
    fd.field('СhronicСonditions').$on('change', showHideСhronicСonditionList);

    //сalling functions on form loading
    showHideAllergicList();
    showHideVaccineReactionList();
    showHideСhronicСonditionList();

});

//show/hide AllergicDetails list
function showHideAllergicList() {
    if (fd.field('Allergic').value == 'Yes') {
        fd.field('AllergicDetails').hidden = false;
    } else {
        fd.field('AllergicDetails').hidden = true;
    }
}

//show/hide Vaccine Reaction list
function showHideVaccineReactionList() {
    if (fd.field('VaccineReaction').value == 'Yes') {
        fd.field('VaccineDetails').hidden = false;
    } else {
        fd.field('VaccineDetails').hidden = true;
    }
}

//show/hide Сhronic Сonditions list
function showHideСhronicСonditionList() {
    if (fd.field('СhronicСonditions').value == 'Yes') {
        fd.field('СhronicСonditionsDetails').hidden = false;
    } else {
        fd.field('СhronicСonditionsDetails').hidden = true;
    }
}

//disable dates to allow only 18+ users
//set up calendar start view
const calendarSetUp = async () => {

    // Load external libraries
    await $.getScript('https://cdn.jsdelivr.net/npm/luxon@3.5.0/build/global/luxon.min.js');

    // Get today's date
    const today = luxon.DateTime.now();

    const newDate = today.minus({
        years: 18
    });

    fd.field('BirthDate').widgetOptions = {
        max: newDate.toJSDate(),
        start: 'decade'
    }
}

