.fd-form h1, .fd-form .h1 {
    font-size: 2.34375rem !important;
}

.fd-form .alert-secondary {
    color: #5d5e60 !important;
}

.fd-form h4, .fd-form .h4 {
    font-size: min(4.5vw, 1.40625rem) !important;
    padding-top: 4px !important;
    padding-bottom: 4px !important;
    margin-bottom: 1rem !important;
}

.fd-form h4:after {
    content: ' ';
    display: block;
    border-top: 3px solid #d0d0d0;
}

.fd-form a.nav-link.active::before {
    content: '\25BC';
    padding-right: 15px;
    padding-left: 0px;
}

.fd-form a.nav-link::before {
    content: '\25B7'; 
    padding-right: 15px;
    padding-left: 0px;
}

.fd-form .card:not(:last-child) {
    border-bottom: none;
}

.fd-form .card-header {
    border-bottom: none;
}
